(function(jQuery){
    jQuery.fn.iconpicker = function(inputSelector){
        jQuery(this).focusin(function(){
            jQuery(".howl-iconpicker-outer").css("display", "table");
            jQuery(".howl-iconpicker .geticonval").removeClass("selectedicon");
            var whichInputClass = jQuery(this).attr("class");
            var whichInputId = jQuery(this).attr("id");
            jQuery(".howl-iconpicker .geticonval").on("click", function(){
                var iconClass = jQuery(this).text();
                var prefix = brandIcons.includes(iconClass) ? "fab fa-" : "fa-";
                jQuery(".howl-iconpicker .geticonval").removeClass("selectedicon");
                jQuery(this).addClass("selectedicon");
                if (jQuery(inputSelector).attr("class") == whichInputClass && jQuery(inputSelector).attr("id") == whichInputId) {
                    jQuery(inputSelector).val(prefix + iconClass).change();
                }
            });
        });
    };
})(jQuery);
